#!/bin/sh
#\n# Configuration\n#\n# This sets up configuration files and environment\n\nset -e\n\necho \"  Configuring environment...\"\n\n# Create .localrc if it doesn't exist\nif [ ! -f \"$HOME/.localrc\" ]; then\n  echo \"  Creating .localrc for local environment variables...\"\n  cat > \"$HOME/.localrc\" << 'EOF'\n# Local environment variables\n# This file is sourced by zshrc and is not tracked in git\n# Add your local/private environment variables here\n\n# Example:\n# export SOME_API_KEY=\"your-key-here\"\n# export LOCAL_PATH=\"/path/to/something\"\n\n# AWS Profile (if using)\n# export AWS_PROFILE=\"your-profile\"\n\n# Add any local PATH modifications\n# export PATH=\"/your/local/path:$PATH\"\nEOF\n  echo \"  Created ~/.localrc - add your local environment variables there\"\nfi\n\n# Set up git-flow completion if git-flow is installed\nif command -v git-flow &> /dev/null; then\n  if [ ! -f \"$HOME/.git-flow-completion.zsh\" ]; then\n    echo \"  Setting up git-flow completion...\"\n    curl -L https://raw.githubusercontent.com/bobthecow/git-flow-completion/master/git-flow-completion.zsh > \"$HOME/.git-flow-completion.zsh\" || {\n      echo \"  Warning: Could not download git-flow completion\"\n    }\n  fi\nfi\n\n# Configure Git if needed\nif [ ! -f \"$HOME/.gitconfig\" ] || ! git config --global user.name > /dev/null 2>&1; then\n  echo \"  Git configuration is incomplete\"\n  echo \"  Please run 'git config --global user.name \"Your Name\"'\"\n  echo \"  Please run 'git config --global user.email \"your.email@example.com\"'\"\nfi\n\n# Set up SSH directory\nmkdir -p \"$HOME/.ssh\"\nchmod 700 \"$HOME/.ssh\"\n\n# Configure zsh as default shell if needed\nif [ \"$SHELL\" != \"$(which zsh)\" ]; then\n  echo \"  Setting zsh as default shell...\"\n  if ! grep -q \"$(which zsh)\" /etc/shells; then\n    echo \"$(which zsh)\" | sudo tee -a /etc/shells\n  fi\n  chsh -s \"$(which zsh)\" || {\n    echo \"  Warning: Could not set zsh as default shell\"\n    echo \"  Please run: chsh -s $(which zsh)\"\n  }\nfi\n\necho \"  Configuration completed\"\nexit 0